import React from 'react'

class Search extends React.Component {
    state = { title: "" }
    onSubmit(event) {
        event.preventDefault();
    }
    onSeachChange(event) {
        const input_title = event.target.value
        console.log(input_title);
        this.setState({ title: input_title });

    }
    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <div class="search">
                    <label for="search-bar">Search :</label>
                    <input onChange={this.onSeachChange} value={this.state.title} type='text' name='search-bar' placeholder='Search your query here' onSubmit={this.onSubmit}></input>
                </div>
            </form>
        )
    }

}

export default Search
